import React, { useRef } from 'react';

import logo from './../images/GitHubLogo.png';
import searchIconSmall from './../images/SearchIconSmall.png';

import Input from './common/Input';

function Nav(){
    const inputEl = useRef(null);
    const onButtonClick = () => {
        // `current` указывает на смонтированный элемент `input`
        console.log('hi')
        inputEl.current.focus();
    };
    return(
        <nav className="nav">
            <div className="container">
                <div className="nav-wrapper">
                    <div className="logo-block">
                        <img src={logo} alt="Logo image"></img>
                    </div>
                    <div onClick={onButtonClick} className="search-block">
                        <img src={searchIconSmall} alt="Search icon"></img>
                        <Input className="search-field" type="text" placeholder="Enter GitHub username" ref={inputEl} />
                    </div>
                </div>
            </div>
        </nav>
    );
}

export default Nav;
