import React,{ useEffect, useState } from "react";

function Repositories(props){
    const token = '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';

    const pagesCount = Math.ceil(props.countRepos / 4);
    const [currentPage, setCurrentPage] = useState(1);

    const [reposData, setReposData] = useState([]);

    useEffect(() => {
        getUserData();
    }, []);

    async function getUserData() {
        fetch(`https://api.github.com/users/${props.username}/repos?per_page=4&page=${currentPage}`, {
            method: 'GET',
            headers: {
                "Content-Type": "application/json",
                "Authorization": `Bearer ${token}`
            }
        })
        .then((response) => {
            return response.json();
        })
        .then((data) => {
            setReposData(data);
        });
    }

    return(
        <div className="repositories">
            <h3>Repositories ({props.countRepos})</h3>
            <ul className="repos-list">
                {reposData.map((item, index) =>
                    <li key={index}>
                        <a href={item.html_url} target="_blank" className="repo-title">{item.name}</a>
                        <p className="repo-desc">{item.description}</p>
                    </li>)}
            </ul>
            <div className="controllers">
                buttons
            </div>
        </div>
    );
}

export default Repositories;
